    public void crearCliente(){
        ClienteController cliente = new ClienteController();
        cliente.crearClienteButton(nombreField.getText(), telefonoField.getText());
        mostrarTextArea.setText("Cliente "+nombreField.getText()+" creado exitosamente ");
    }
    
    public void leerCliente(){
                DefaultTableModel model = (DefaultTableModel) clienteTable1.getModel();//instanciar la tabla
        model.setRowCount(0);//resetea la tabla
        ArrayList<Cliente> clientes = new ArrayList();//intanciar una lista arreglo
        ClienteController cliente = new ClienteController ();//insanciando e controller
        List<Cliente> leer = cliente.leerClientesButton();//consultado los datos y asignandolos
        clientes.addAll(leer);//asignar a la lista arreglo la lista para que la tabla acepte los datos
        
        Object columnas[] = new Object[3];//crea una array c
        for(Cliente c : clientes){
            columnas[0]=c.getIdClient();
            columnas[1]=c.getNombreCliente();
            columnas[2]=c.getTelefono();
            model.addRow(columnas);
        }
        
    }
    
    public void actualizarCliente(){
        ClienteController cliente = new ClienteController();
        cliente.actualizarClienteButton(Integer.parseInt(idField.getText()),nombreField.getText(),telefonoField.getText() );
        mostrarTextArea.setText("Cliente "+idField.getText()+". "+nombreField.getText()+" actualizado exitosamente ");
    }
    
    public void eliminarCliente(){
        ClienteController cliente = new ClienteController();
        cliente.eliminarClienteButton(Integer.parseInt(idField.getText()));
        mostrarTextArea.setText("Ciente "+idField.getText()+" eliminado exitosamente ");
    }
 

